class Create extends DataTransformerAbstract
{
    
    /**
     * Attribute constructor.
     *
     * @param string $code
     * @param string $label
     * @param array  $options
     */
    public function __construct($code, $label, array $options = [])
    {
        $this->setOutputData([
            'attribute' => [
                'name' => $code,
                'label' => $label,
                'options' => $options
            ]
        ]);
        
        parent::__construct();
    }
}
